ncid = netcdf.open('HadCRUT.5.0.1.0.analysis.anomalies.ensemble_mean.nc');ncid2 = netcdf.open('SAT.mean.lgm.nc');ncid3 = netcdf.open('SAT.sd.lgm.nc');T_blended_HadCRUT5 =  netcdf.getVar(ncid,0);T_blended_HadCRUT5(T_blended_HadCRUT5 <= -1000) = NaN;T_HadCRUT5_lat_means = mean(T_blended_HadCRUT5,'omitnan');Annual_means = zeros([36 171]);for i=1:171 %years    for j=1:12 %months        for k=1:36 %latitudinal bands            Annual_means(k,i) =  Annual_means(k,i) + T_HadCRUT5_lat_means(1,k,12*(i-1)+j)/12;        end    endendMean_1850_1900 = zeros(1,36);Mean_1970_2020 = zeros(1,36);Mean_1880_1930 = zeros(1,36);for i=1:51    for j=1:36        Mean_1850_1900(j) = Mean_1850_1900(j) + Annual_means(j,i)/51;        Mean_1970_2020(j) = Mean_1970_2020(j) + Annual_means(j,i+1970-1850)/51;        Mean_1880_1930(j) = Mean_1880_1930(j) + Annual_means(j,i+1880-1850)/51; ;    endendMean_2000_2020 = zeros(1,36);Mean_1900_1920 = zeros(1,36);for i=1:21    for j=1:36                Mean_2000_2020(j) = Mean_2000_2020(j) + Annual_means(j,i+2000-1850)/51;        Mean_1900_1920(j) = Mean_1900_1920(j) + Annual_means(j,i+1900-1850)/51; ;    endendDeltaT_LGM_ensemble_mean =  netcdf.getVar(ncid2,2);DeltaT_LGM_ensemble_sigma =  netcdf.getVar(ncid3,2);LGM_phi_deg = netcdf.getVar(ncid2,1);DeltaT_LGM_ensemble_zonal_mean = linspace(0,0,90);DeltaT_LGM_ensemble_zonal_sigma = linspace(0,0,90);for i=1:180    for j=1:90        DeltaT_LGM_ensemble_zonal_mean(j) = DeltaT_LGM_ensemble_zonal_mean(j) + DeltaT_LGM_ensemble_mean(i,j)/180;        DeltaT_LGM_ensemble_zonal_sigma(j) = DeltaT_LGM_ensemble_zonal_sigma(j) + DeltaT_LGM_ensemble_sigma(i,j)/180;;    endendphi_HadCRUT5 = linspace(-87.5,87.5,36);Normalised_LGM_amp = DeltaT_LGM_ensemble_zonal_mean / (0.5*(DeltaT_LGM_ensemble_zonal_mean(45) + DeltaT_LGM_ensemble_zonal_mean(46)));Normalised_LGM_amp_low = (DeltaT_LGM_ensemble_zonal_mean-DeltaT_LGM_ensemble_zonal_sigma) / (0.5*(DeltaT_LGM_ensemble_zonal_mean(45) + DeltaT_LGM_ensemble_zonal_mean(46)));Normalised_LGM_amp_high = (DeltaT_LGM_ensemble_zonal_mean+DeltaT_LGM_ensemble_zonal_sigma) / (0.5*(DeltaT_LGM_ensemble_zonal_mean(45) + DeltaT_LGM_ensemble_zonal_mean(46)));%