ncid_PETM = netcdf.open('PETMDA_ATM_annual.nc');lat_PETM =  netcdf.getVar(ncid_PETM,0);lon_PETM=netcdf.getVar(ncid_PETM,2);T_PETM= netcdf.getVar(ncid_PETM,3);sigma_T_PETM = netcdf.getVar(ncid_PETM,4);T_PETM_Zonal_Annual_means = zeros([96 1]);T_prePETM_Zonal_Annual_means = zeros([96 1]);sigma_T_PETM_Zonal_Annual_Means = zeros([96 1]);sigma_preT_PETM_Zonal_Annual_Means = zeros([96 1]);for i=1:144 %longitude    for j=1:96 %latitude        T_PETM_Zonal_Annual_means(j) = T_PETM_Zonal_Annual_means(j) + T_PETM(i,j,1)/144;        T_prePETM_Zonal_Annual_means(j) = T_prePETM_Zonal_Annual_means(j) + T_PETM(i,j,2)/144;        sigma_T_PETM_Zonal_Annual_means(j) = sigma_T_PETM_Zonal_Annual_Means(j) + sigma_T_PETM(i,j,1)/180;;        sigma_preT_PETM_Zonal_Annual_means(j) = sigma_preT_PETM_Zonal_Annual_Means(j) + sigma_T_PETM(i,j,2)/180;;    endend